using System;using ASC.Common.Context;using ASC.Common.Module;using ASC.Common.Security.Authentication;using ASC.Common.Services;namespace ASC.Runtime.Remoting {				[Serializable]	public class RemotingCallStackFrame {								public DateTime CallTime { get; private set; }								public IModulePartInfo ModulePartInfo { get; private set; }								public string HostName { get; private set; }								public IAccount Account { get; private set; }								public IServiceInfo ServiceInfo { get; private set; }								public string Method { get; private set; }								public AppContext AppContext { get; private set; }																				public RemotingCallStackFrame(			DateTime callTime,			IAccount account,			string hostName,			string method,			IModulePartInfo modulePartInfo,			IServiceInfo serviceInfo) {			CallTime = callTime;			Account = account;			HostName = hostName;			Method = method;			ModulePartInfo = modulePartInfo;			ServiceInfo = serviceInfo;		}										public override string ToString() {			return String.Format(					"[{2}]:\"{3}\" under \"{0}\" call \"{4}\" from \"{1}\"",					Account,					HostName,					CallTime,					ModulePartInfo,					Method				);		}	}}